    Persona getByUsernameEquals(String username);
    List<Persona> findAllByIdPersonaEquals(Long idPersona);


    default  String getClaveAleatoria(){
        String resultado = "";
        for (int i = 1; i <= 8; i++) {
            resultado = resultado + (int) (Math.random() * 50 + 1);
        }
        return resultado;
    }
    default String retornoToken(Persona usuario){
        ObjectMapper mapper = new ObjectMapper();
        String result = null;
        try {
            result = mapper.writeValueAsString(usuario);
        } catch (JsonProcessingException e) {
            e.printStackTrace();
        }
        return Jwts.builder()
                .setSubject(result)
                .setExpiration(new Date(System.currentTimeMillis()+180000))
                .signWith(SignatureAlgorithm.HS512, "Pl@tonSoft")
                .compact();
    }

    default Persona retornoPersona(String token){
        String userJson = Jwts.parser()
                .setSigningKey("Pl@tonSoft")
                .parseClaimsJws(token)
                .getBody()
                .getSubject();
        return (new Gson()).fromJson(userJson,Persona.class);
    }

    default Persona statusSession(HttpHeaders headers){
        try{
            String token =  String.valueOf(headers.get("TokenSystig")).replace("[","").replace("]","");
            Persona usuarioActivo = retornoPersona(token);
            return usuarioActivo;
        }catch (Exception e){
            e.printStackTrace();
            return null;
        }
    }

